public class WorkingArithmetic {
    public static void main(String[] args) {
        int sum = 3 + 2;      // iadd
        int diff = 3 - 1;     // isub
        int product = 2 * 3;  // imul
        
        System.out.println(sum);
        System.out.println(diff); 
        System.out.println(product);
    }
}